mapper.select:
SELECT *
FROM event
WHERE announce_start_at <= #{now}
  AND announce_end_at >= #{now}
ORDER BY event_id DESC
LIMIT 1

mapper.update:
UPDATE event
SET status = #{status}
WHERE event_id = #{eventId}

UPDATE event
SET status = 'ACTIVE'
WHERE status = 'READY'
AND start_at <= #{now}
AND end_at   >  #{now}
